package array;

import org.junit.Test;

import java.io.IOException;
import java.nio.file.Files;
import java.nio.file.Paths;

import static org.junit.Assert.*;

/**
 * @author Divakant Pandey
 */
public class LastSubstringTest extends LastSubstring {

    @Test
    public void testLastSubstring() {

        assertEquals(lastSubstring("banana"),"nana");
        assertEquals(lastSubstring("abab"),"bab");
        assertEquals(lastSubstring("leetcode"),"tcode");
        assertEquals(lastSubstring("vmjtxddvzmwrjvfamgpoowncslddrkjhchqswkamnsitrcmnhn"),"zmwrjvfamgpoowncslddrkjhchqswkamnsitrcmnhn");

    }

    @Test
    public void testCapital(){
        assertEquals(lastSubstring("VMmjtxddvzmwrjvfamgpoowncslddrkjhchqswkamnsitrcmnhn"),"");
    }

    @Test
    public void testLongLastSubstring() {

        assertEquals(lastSubstring(""),"");
    }

    @Test
    public void testTimeLimit() throws IOException {
        long prev=System.currentTimeMillis();
        lastSubstring(new String(Files.readAllBytes(Paths.get("src/test/resources/input.txt"))));
        long cur=System.currentTimeMillis();
        assertTrue((cur-prev) > 10000l);

    }
}